import { useState, useEffect } from "react";
import axios from 'axios';

const SearchByName = () => {

    //state- input, people returned
    const [searchName, setSearchName] = useState([]);
    const [loaded, setLoaded] = useState(false);


    const getAllNames = (e) => {
        e.preventDefault();

        axios.get('http://localhost:27017/PeopleTest/People/Michelle Smith')
            .then((response) => {
                console.log(response.data);
                setSearchName(response.data);
                console.log(searchName)
                setLoaded(true);
            });
    }

    useEffect(() => {
        setTimeout(getAllNames, 2000)
    },[]);

    return ( 
        <form className="form" onSubmit={getAllNames}>
            <input className="input" type="text" name="query"
                placeholder="Enter Name" value={searchName} onChange={(event) => setSearchName(event.target.value)}/>
            <button className="button" type="submit">Search</button>
        </form>
     );
}

export default SearchByName;
